mapper.select:
select place_review_number, place_review_contents, place_review_rating, place_review_dateTime, user_number, user_name, place_review_helful
from tbl_place_review
where place_id = #{placeId}

mapper.update:

update tbl_place_review
set place_review_helful =place_review_helful+1
where place_review_number = #{placeReviewNumber}


update tbl_place_review
set place_review_helful =place_review_helful-1
where place_review_number = #{placeReviewNumber}
